import psycopg2con = Nonecon = psycopg2.connect(host='bocian', database='alignment', user='aligner_admin', password='default99')cur = con.cursor()cur.execute("CREATE TABLE system_template(id BIGSERIAL PRIMARY KEY, name VARCHAR(50), file VARCHAR(1000))")cur.execute("CREATE TABLE system_setting(id BIGSERIAL PRIMARY KEY, name VARCHAR(50), template_id BIGINT references system_template(id), cmtk_initial_var VARCHAR(100), cmtk_affine_var VARCHAR(100), cmtk_warp_var VARCHAR(100), cmtk_align_var VARCHAR(100), initial_pass_level VARCHAR(10), final_pass_level VARCHAR(10), auto_balance_th VARCHAR(10))")cur.execute("CREATE TABLE images_alignment(id BIGSERIAL PRIMARY KEY, name VARCHAR(500), alignment_stage INT, loading_host VARCHAR(100), last_host VARCHAR(100), max_stage INT, original_ext VARCHAR(10), original_path VARCHAR(1000), orig_orientation VARCHAR(50), temp_initial_nrrd VARCHAR(1000), aligned_sg VARCHAR(1000), temp_initial_score VARCHAR(20), aligned_slice_score VARCHAR(20), aligned_score VARCHAR(20), aligned_avgslice_score VARCHAR(20), aligned_ac1 VARCHAR(1000), ac1_channel INT, aligned_bg VARCHAR(1000), background_channel INT, signal_channel INT, settings_id bigint references system_setting(id) )")cur.execute("CREATE TABLE images_original_nrrd(id BIGSERIAL PRIMARY KEY, image BIGINT references images_alignment(id), channel INT, new_min INT, new_max INT, file VARCHAR(1000), is_index BOOLEAN, pre_hist integer ARRAY[255])")cur.execute("CREATE TABLE system_server(id BIGSERIAL PRIMARY KEY, use_db VARCHAR(100), host_name VARCHAR(50), run_stages integer ARRAY[20], temp_dir VARCHAR(1000), cmtk_dir VARCHAR(1000), template_dir VARCHAR(1000), active BOOLEAN)")con.commit()con.close()